class Person:
    def __init__(self, name, age):
        self.name = name
        self.age = age

def is_prime(n):
    if n <= 1:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True

class Car:
    def __init__(self, make, model, year):
        self.make = make
        self.model = model
        self.year = year

def factorial(n):
    if n == 0:
        return 1
    else:
        return n * factorial(n-1)
